Widget filterIcon(context) {
  return IconButton(
      icon: Icon(Icons.filter_alt_rounded),
      onPressed: (() => {
            Navigator.push(context,
                MaterialPageRoute(builder: (context) => SearchTripsWidget()))
          }));
}
